sys_variable_value.value:
(function execute(inputs, outputs) {
	try {
        var results = JSON.parse(inputs.details);

        // Sort the results based on the total execution speed
        results.sort((a, b) => a.total_execution_time_MS - b.total_execution_time_MS);

		var ran_by_user = inputs.test_definition.getDisplayValue('last_run_by');

        // CSS styling is set in a system property
        var style_css = gs.getProperty('x_1397355_s_speed.result.style', '');
        var html = style_css + '<container>';

        html += '<div id="results_summary">Test ran by <strong>'+ ran_by_user + '</strong> at ' + inputs.test_definition.sys_updated_on;
        html += '<br><br>Fastest script is <strong>' + results[0].script_name + '</strong> with an average execution time of ' + results[0].average_time_MS + ' milliseconds';
        html += '<br>Slowest script is <strong>' + results[results.length - 1].script_name + '</strong> with an average execution time of ' + results[results.length - 1].average_time_MS + ' milliseconds';
        html += '<br><br>Scripts in order from fastest to slowest. Iterations: ' +  results[0].iterations + '. Times are in milliseconds.';
		html += '</div>'
        
        // Set up a result table and columns
        html += '<table id="results_table">';
        html += '<thead>';
        html += '<tr>';
        html += '<th>Script</th>';
        html += '<th>Script #</th>';
        html += '<th>Average Time</th>';
        html += '<th>Total Execution Time</th>';
        html += '<th>Slower than Fastest, %</th>';
        html += '<th>Execution Times</th>';
        html += '</tr>';
        html += '</thead>';
        html += '<tbody>';

        // Print the results to the table
        var el = {}, time_diff_to_fastest = 0, fastest_avg = results[0].average_time_MS;
        for (i = 0; i < results.length; i++) {
            el = results[i];
            if (i != 0) {
                time_diff_to_fastest = Math.round(((el.average_time_MS - fastest_avg) / fastest_avg) * 100);
            };

            html += '<tr>';
            html += '<td>' + el.script_name + '</td>';
            html += '<td>' + el.script_field_name.substring(el.script_field_name.length - 1, el.script_field_name.length) + '</td>';
            html += '<td>' + el.average_time_MS + '</td>';
            html += '<td>' + el.total_execution_time_MS + '</td>';
            html += '<td>' + time_diff_to_fastest + '</td>';
            html += '<td>' + el.execution_time_per_iteration.join(', ') + '</td>';
            html += '</tr>';
        }

        html += '</tbody>';
        html += '</table>';

        // Include the scripts that were used
        html += '<br><br><div id="scripts">Scripts used:';
        for (i = 1; i <= results.length; i++) {
            var def_gr = inputs.test_definition;
			html += '<pre class="script">';
			html += '<span class="script_name">' + results[i - 1].script_name + '</span><br>';
			html += escapeHtmlCharacters(def_gr.getValue('script_' + i)); // Escape the JS characters so it won't mess up the HTML
			html += '</pre><br>';
        }

        html += '</div>';
        html += '</container>';

        outputs.html = html;
		outputs.success = true;
    } catch (ex) {
		gs.error('Error in Flow Action "Write Script Test Results":\n' + ex)
		outputs.error_details = ex.message;
		outputs.success = false;
	}
})(inputs, outputs);

function escapeHtmlCharacters(script) {
    return script.replace(/[&<>"'`=\/]/g, function(char) {
        return {
            '&': '&amp;',
            '<': '&lt;',
            '>': '&gt;',
            '"': '&quot;',
            "'": '&#39;',
            '`': '&#x60;',
            '=': '&#x3D;',
            '/': '&#x2F;'
        }[char];
    });
}

