sys_variable_value.value:
(function execute(inputs, outputs) {
	try {
		var number_of_scripts = parseInt(inputs.test_definition.getValue('number_of_scripts'), 10);
		if (gs.nil(number_of_scripts)) {
			throw 'Missing "number_of_scripts", cannot continue';
		}

		// Default to 10 iterations if missing from the definition
		var iterations = parseInt(inputs.test_definition.getValue('iterations'), 10) || 10;

		// Initialize variables and GlideScopedEvaluator
		scripts = [], script_name = '', i = 0, glide_evaluator = new GlideScopedEvaluator();

		// Loop through the selected number of scripts and also check if script is defined, start count from 1 because fields are named from _1
		for (i = 1; i <= number_of_scripts; i++) {
			// Set a fallback value if script name is not defined
			script_name = inputs.test_definition.getValue('script_name_' + i) || 'script_name_' + i;

			// Check if the script field is empty
			if (gs.nil(inputs.test_definition.getValue('script_' + i))) {
				throw 'Missing "script_' + i + '", cannot continue';
			}

			// Construct the script execution objects
			scripts.push(new ScriptSpeedExecution(iterations, 'script_' + i, script_name));
		}

		// Start looping through the iterations and running the scripts
		for (i = 0; i < iterations; i++) {
			scripts.forEach(function (script) {
				script.timer_start_time = new Date(); // Start time of script execution

				// Need to use GlideScopedEvaluator to run the scripts since they are passed from record and not given within this same script
				glide_evaluator.evaluateScript(inputs.test_definition, script.script_field_name, {
					success: true
				});

				if (!glide_evaluator.getVariable('success')) {
					throw 'Failed to execute script "' + script.script_name + '", check your script and retry'
				}

				script.timer_end_time = new Date(); // End time of script execution
				script.elapsed_time_MS = script.timer_end_time - script.timer_start_time; // Calculate elapsed time by subtract starting value from final value
				script.execution_time_per_iteration.push(script.elapsed_time_MS);
			});
		}

		// After all the iterations count total and average times + remove the result of the last iteration
		scripts.forEach(function (script) {
			script.total_execution_time_MS = script.get_total_elapsed_time();
			script.average_time_MS = script.get_average();
			delete script.elapsed_time_MS;
		});

		outputs.script_execution_details = JSON.stringify(scripts);
		outputs.success = true;
	} catch (ex) {
		gs.error('Error in Flow Action "Execute Scripts":\n' + ex)
		outputs.success = false
		outputs.error_details = ex.message;
	}
})(inputs, outputs);

